    post_id NUMBER REFERENCES posts(id),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);


CREATE SEQUENCE post_likes_seq
    START WITH 31
    INCREMENT BY 1
    NOCACHE;


COMMENT ON TABLE post_likes IS 'Table for storing post like information';
COMMENT ON COLUMN post_likes.id IS 'Unique identifier for each like action';
